import React from 'react'
import FirstPage from './FirstPage';
import PracticeScript from './PracticeScript';
import './WholePage.css';

class WholePage extends React.Component {
    constructor(props){
        super(props);
        this.state = {script: '',
                      showFirstPage: true,
                      minutes: -1,
                    //   seconds: -1,
                    };
        this.storeScript = this.storeScript.bind(this);
        this.storeMinutes = this.storeMinutes.bind(this);
    }                                       

    storeMinutes = e => {
        this.setState({minutes: e.target.value});
    }

    storeScript = e => {    
        e.preventDefault();
        this.setState({script: document.getElementById('script-text-area').value, 
                    showFirstPage: false,
                });
    }

    render() {
        const showFirstPage = this.state.showFirstPage;
        let pageToShow;

        if(showFirstPage){
            pageToShow = <FirstPage storeScript={this.storeScript} showFirstPage={this.state.showFirstPage} />;
        }else{
            pageToShow = <PracticeScript script={this.state.script} />
        }

        return(
            <div className='highest-div'>
                {pageToShow}
            </div>
        );
    }
}

export default WholePage;
