import React from 'react'
import './FirstPage.css'

class FirstPage extends React.Component {

    constructor(props){
        super(props);
        this.state = {script: ''};
    }

    storeScript = (e) =>{
        this.setState({script: document.getElementById('script-text-area').value})
    }

    render() {
        return(
            <div className="intro-page-div">
                <h1>No hassle, just copy paste your script</h1>
                <form>
                    <textarea id="script-text-area" rows="12" cols="70"></textarea>
                    <div>
                        <input onClicktype="submit" value="Submit" />
                    </div>
                </form>
            </div>
        );
    }
}

export default FirstPage;
