import React from 'react'
import FirstPage from './FirstPage';
import PracticeScript from './PracticeScript';

class WholePage extends React.Component {
    constructor(props){
        super(props);
        this.state = {script: '',
                      showFirstPage: true,
                      minutes: -1,
                      seconds: -1,
                    };
        this.storeScript = this.storeScript.bind(this);
    }

    storeScript = (e) =>{   
        e.preventDefault()
        this.setState({script: document.getElementById('script-text-area').value, showFirstPage: false});
        console.log(document.getElementById('script-text-area').value);
        console.log("whatup")
        console.log(this.state.script);
    }

    render() {
        const showFirstPage = this.state.showFirstPage;
        let pageToShow;

        if(showFirstPage){
            pageToShow = <FirstPage storeScript={this.storeScript} showFirstPage={this.state.showFirstPage} />;
        }else{
            pageToShow = <PracticeScript script={this.script} />
        }

        return(
            <div>
                {pageToShow}
            </div>
        );
    }
}

export default WholePage;
