import React from 'react';

class Stopwatch extends React.Component {
    constructor(props){
        super(props);
        this.countTime = this.countTime.bind(this);
        this.state = {minutes: 0, 
            seconds: 0,
        };
        this.count = '';
    }

    countTime = () => {
        if(this.state.seconds === 59){
            this.setState({minutes: this.state.minutes + 1, seconds: -1});
        }
        this.setState({seconds: this.state.seconds + 1})
        if(this.state.minutes === this.props.totalMinutes){
            clearInterval(this.count);
        }
    }

    componentDidMount = () => {
        this.count = setInterval(this.countTime, 1000);
    }

    render() {
        // let count = setInterval(this.countTime, 1000);
        return(
            <div className="stopwatch">
                <p>{this.state.minutes} : {this.state.seconds}</p>
            </div>
        );
    }
}

export default Stopwatch;
