import React from 'react'
import './FirstPage.css'

let minutesArr = [];
for(let i=0; i<10; i++){
    minutesArr[i] = {value: i+1, 
                    valuePlusHalf: i+1+.5, 
                    valuePlusHalfstr: (i+1).toString() + "1/2",
                };
};

class FirstPage extends React.Component {
    render() {
        return(
            // {this.state.shop.map((item, key) =>
            //     <Item item={item} key={item.id} />
            // )}
            <div className="intro-page-div">
                <h1>No hassle, just copy paste your script</h1>
                <form>
                    <textarea id="script-text-area" rows="12" cols="70"></textarea>
                    <select name="minutes">
                        {minutesArr.map((minute) =>
                            <option value={minute.value} key={minute.toString()}>{minute.value}</option>
                            //<option value={minute.valuePlusHalf} key={minute.valuePlusHalf.toString()}>{minute.valuePlusHalfstr}</option>
                        )}
                    </select>
                    <div>
                        <input onClick={this.props.storeScript} type="submit" value="Submit" />
                    </div>
                </form>
            </div>
        );
    }
}

export default FirstPage;
