import React from 'react';
import './PracticeScript.css';
import Stopwatch from './Stopwatch';

let watch;
class PracticeScript extends React.Component {
    constructor(props){
        super(props);
        this.highlight = this.highlight.bind(this);
    }

    highlight = e => {
        document.getElementById('paragraph').className += " highlight";
        watch = <Stopwatch go={true} />
    }

    render() {
        watch = <Stopwatch go={false} />
        return(
            <React.Fragment>
                <div className="text-container">
                    <p id='paragraph' className='script'>{this.props.script}</p>
                </div>
                <button className='button' onClick={this.highlight} type='button'>Start</button>
                {watch}
            </React.Fragment>
        );
    }
}

export default PracticeScript;
