import React from 'react'
import FirstPage from './FirstPage';

class WholePage extends React.Component {
    constructor(props){
        super(props);
        this.state = {script: '',
                      showFirstPage: true,
                    };
        this.storeScript = this.storeScript.bind(this);
    }

    storeScript = (e) =>{   
        e.preventDefault()
        this.setState({script: document.getElementById('script-text-area').value});
        console.log(this.state.script);
        console.log("hi");
    }

    render() {
        const showFirstPage = this.state.showFirstPage;
        let pageToShow;

        if(showFirstPage){
            pageToShow = <FirstPage storeScript={this.storeScript} />;
        }

        return(
            <div>
                {pageToShow}
            </div>
        );
    }
}

export default WholePage;
