import React from 'react';

class Stopwatch extends React.Component {
    constructor(props){
        super(props);
        this.countTime = this.countTime.bind(this);
        this.minutes = 0;
        this.seconds = 0;
    }

    countTime = () => {
        if(this.seconds === 59){
            this.minutes++;
            this.seconds = -1;
        }
        this.seconds++;
        if(this.minutes === this.props.totalMinutes){
            return;
        }

    }

    render() {
        if(this.props.go){
            let count = setInterval(this.countTime, 1000);
            // console.log("hi");
        }
        // console.log(this.props.go);
        return(
            <div className="stopwatch">
                <p>{this.minutes} : {this.seconds}</p>
            </div>
        );
    }
}

export default Stopwatch;
