import React from 'react'
import './FirstPage.css'

let minutesArr = [];
for(let i=0; i<10; i++){
    minutesArr[i] = {value: i+1, 
                    valuePlusHalf: i+1+.5, 
                };
};

class FirstPage extends React.Component {
    render() {
        return(
            <div className="intro-page-div">
                <h1>No hassle, just copy paste your script</h1>
                <form>
                    <textarea id="script-text-area" rows="12" cols="70"></textarea>
                    <h2>How many minutes?</h2>
                    <select className="minutes" name="minutes">
                        {minutesArr.map((minute) =>
                            <React.Fragment key={minute.value.toString()}>
                                <option value={minute.value}>{minute.value}</option>
                                <option value={minute.valuePlusHalf}>{minute.valuePlusHalf}</option>
                            </React.Fragment>
                        )}
                    </select>
                    <div>
                        <input onClick={this.props.storeScript} type="submit" value="Submit" />
                    </div>
                </form>
            </div>
        );
    }
}

export default FirstPage;
